                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-sm text-gray-500">
                            T{agent.tier_level}.{agent.tier_position}
                          </span>
                          {modalType === 'edit' && (
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => handleRemoveAgent(editingItem.id, agent.extension_id)}
                              className="text-red-600 hover:text-red-700"
                            >
                              <UserMinus className="h-4 w-4" />
                            </Button>
                          )}
                        </div>
                      </div>
                    ))}
                    
                    {modalType === 'edit' && (
                      <div className="pt-2">
                        <Select onValueChange={(value) => {
                          if (value) {
                            handleAddAgent(editingItem.id, value);
                          }
                        }}>
                          <SelectTrigger>
                            <SelectValue placeholder="Add agent..." />
                          </SelectTrigger>
                          <SelectContent>
                            {extensions
                              .filter(ext => !editingItem.agents.some(agent => agent.extension_id === ext.id))
                              .map((ext) => (
                                <SelectItem key={ext.id} value={ext.id}>
                                  {ext.extension} - {ext.display_name}
                                </SelectItem>
                              ))}
                          </SelectContent>
                        </Select>
                      </div>
                    )}
                  </div>
                </div>
              )}

              {/* Modal Actions */}
              <div className="flex items-center justify-end space-x-2 pt-4 border-t">
                <Button
                  variant="outline"
                  onClick={() => {
                    setShowModal(false);
                    setFormData({});
                    setEditingItem(null);
                  }}
                >
                  Cancel
                </Button>
                {modalType !== 'view' && (
                  <Button
                    onClick={modalType === 'create' ? handleCreate : handleEdit}
                    disabled={loading}
                  >
                    {loading ? 'Saving...' : modalType === 'create' ? 'Create' : 'Update'}
                  </Button>
                )}
              </div>
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
}

