import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { 
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { 
  Users, 
  Search, 
  ArrowLeft,
  UserCheck,
  Edit,
  Trash2,
  AlertCircle,
  Shield,
  Building2
} from 'lucide-react';
import apiClient from '@/lib/api';

interface User {
  id: string;
  email: string;
  first_name: string;
  last_name: string;
  role: string;
  status: string;
  tenant_id: string;
  tenant_name: string;
  tenant_slug: string;
  last_login?: string;
  created_at: string;
}

export default function SuperAdminUsers() {
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedTenant, setSelectedTenant] = useState<string>('all');
  const [showImpersonateModal, setShowImpersonateModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [impersonating, setImpersonating] = useState(false);
  const navigate = useNavigate();
  const { tenantId } = useParams();

  useEffect(() => {
    loadUsers();
  }, [tenantId]);

  const loadUsers = async () => {
    try {
      setLoading(true);
      const response = await apiClient.getCrossTenantUsers();
      if (response.success) {
        setUsers(response.data || []);
      } else {
        throw new Error(response.error || 'Failed to load users');
      }
    } catch (err: any) {
      setError(err.message || 'Failed to load users');
    } finally {
      setLoading(false);
    }
  };

  const handleImpersonate = async (user: User) => {
    setSelectedUser(user);
    setShowImpersonateModal(true);
  };

  const confirmImpersonate = async () => {
    if (!selectedUser) return;

    try {
      setImpersonating(true);
      const response = await apiClient.impersonateUser(selectedUser.tenant_id, selectedUser.id);

      if (response.success && response.data?.token) {
        // Start impersonation
        apiClient.startImpersonation(response.data.token);

        // Store impersonation info
        localStorage.setItem('impersonation_info', JSON.stringify({
          originalUser: selectedUser,
          impersonatedAt: new Date().toISOString()
        }));

        // Redirect to tenant dashboard
        navigate(`/${selectedUser.tenant_slug}/dashboard`);
      } else {
        throw new Error(response.error || 'Failed to impersonate user');
      }
    } catch (err: any) {
      setError(err.message || 'Failed to impersonate user');
    } finally {
      setImpersonating(false);
      setShowImpersonateModal(false);
    }
  };

  const getRoleBadge = (role: string) => {
    switch (role) {
      case 'super_admin':
        return <Badge variant="default" className="bg-purple-100 text-purple-800">Super Admin</Badge>;
      case 'tenant_admin':
        return <Badge variant="default" className="bg-blue-100 text-blue-800">Tenant Admin</Badge>;
      case 'user':
        return <Badge variant="secondary">User</Badge>;
      default:
        return <Badge variant="outline">{role}</Badge>;
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'active':
        return <Badge variant="default" className="bg-green-100 text-green-800">Active</Badge>;
      case 'inactive':
        return <Badge variant="destructive">Inactive</Badge>;
      case 'suspended':
        return <Badge variant="destructive">Suspended</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const filteredUsers = users.filter(user => {
    const matchesSearch = 
      user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.first_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.last_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.tenant_name.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesTenant = selectedTenant === 'all' || user.tenant_id === selectedTenant;

    return matchesSearch && matchesTenant;
  });

  const uniqueTenants = Array.from(new Set(users.map(user => user.tenant_id)))
    .map(id => users.find(user => user.tenant_id === id))
    .filter(Boolean) as User[];

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading users...</p>
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="mb-6">
          <h1 className="text-2xl font-bold text-gray-900">User Management</h1>
          <p className="text-gray-600">Manage users across all tenants</p>
        </div>

        {error && (
          <Alert variant="destructive" className="mb-6">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>{error}</AlertDescription>
          </Alert>
        )}

        {/* Search and Filters */}
        <Card className="mb-6">
          <CardContent className="pt-6">
            <div className="flex items-center space-x-4">
              <div className="flex-1">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                  <Input
                    placeholder="Search users by name, email, or tenant..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10"
                  />
                </div>
              </div>
              <div className="w-64">
                <Label htmlFor="tenant-filter">Filter by Tenant</Label>
                <select
                  id="tenant-filter"
                  value={selectedTenant}
                  onChange={(e) => setSelectedTenant(e.target.value)}
                  className="w-full mt-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="all">All Tenants</option>
                  {uniqueTenants.map((user) => (
                    <option key={user.tenant_id} value={user.tenant_id}>
                      {user.tenant_name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Users Table */}
        <Card>
          <CardHeader>
            <CardTitle>All Users ({filteredUsers.length})</CardTitle>
            <CardDescription>Manage user accounts and permissions across tenants</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b">
                    <th className="text-left py-3 px-4">User</th>
                    <th className="text-left py-3 px-4">Role</th>
                    <th className="text-left py-3 px-4">Tenant</th>
                    <th className="text-left py-3 px-4">Status</th>
                    <th className="text-left py-3 px-4">Last Login</th>
                    <th className="text-left py-3 px-4">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredUsers.map((user) => (
                    <tr key={user.id} className="border-b hover:bg-gray-50">
                      <td className="py-3 px-4">
                        <div>
                          <div className="font-medium">{user.first_name} {user.last_name}</div>
                          <div className="text-sm text-gray-500">{user.email}</div>
                        </div>
                      </td>
                      <td className="py-3 px-4">
                        {getRoleBadge(user.role)}
                      </td>
                      <td className="py-3 px-4">
                        <div>
                          <div className="font-medium">{user.tenant_name}</div>
                          <div className="text-sm text-gray-500">{user.tenant_slug}</div>
                        </div>
                      </td>
                      <td className="py-3 px-4">
                        {getStatusBadge(user.status)}
                      </td>
                      <td className="py-3 px-4">
                        {user.last_login ? (
                          <div>
                            <div className="text-sm">{new Date(user.last_login).toLocaleDateString()}</div>
                            <div className="text-xs text-gray-500">
                              {new Date(user.last_login).toLocaleTimeString()}
                            </div>
                          </div>
                        ) : (
                          <span className="text-gray-400">Never</span>
                        )}
                      </td>
                      <td className="py-3 px-4">
                        <div className="flex space-x-2">
                          {user.role !== 'super_admin' && (
                            <Button 
                              size="sm" 
                              variant="outline"
                              onClick={() => handleImpersonate(user)}
                            >
                              <UserCheck className="h-3 w-3 mr-1" />
                              Impersonate
                            </Button>
                          )}
                          <Button 
                            size="sm" 
                            variant="outline"
                            onClick={() => navigate(`/edgvoip/users/${user.id}/edit`)}
                          >
                            <Edit className="h-3 w-3 mr-1" />
                            Edit
                          </Button>
                          {user.role !== 'super_admin' && (
                            <Button 
                              size="sm" 
                              variant="outline"
                              onClick={() => {
                                if (confirm('Are you sure you want to delete this user?')) {
                                  // TODO: Implement delete user
                                }
                              }}
                            >
                              <Trash2 className="h-3 w-3 mr-1" />
                              Delete
                            </Button>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Impersonation Confirmation Modal */}
      <Dialog open={showImpersonateModal} onOpenChange={setShowImpersonateModal}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Confirm Impersonation</DialogTitle>
            <DialogDescription>
              You are about to impersonate this user. This will log you in as them and give you access to their tenant.
            </DialogDescription>
          </DialogHeader>

          {selectedUser && (
            <div className="space-y-4">
              <div className="bg-gray-50 p-4 rounded-lg">
                <div className="flex items-center space-x-3">
                  <div className="p-2 bg-blue-100 rounded-full">
                    <Shield className="h-5 w-5 text-blue-600" />
                  </div>
                  <div>
                    <h4 className="font-medium">{selectedUser.first_name} {selectedUser.last_name}</h4>
                    <p className="text-sm text-gray-500">{selectedUser.email}</p>
                  </div>
                </div>
                <div className="mt-3 flex items-center space-x-4">
                  <div className="flex items-center space-x-2">
                    <Building2 className="h-4 w-4 text-gray-400" />
                    <span className="text-sm">{selectedUser.tenant_name}</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Users className="h-4 w-4 text-gray-400" />
                    <span className="text-sm">{getRoleBadge(selectedUser.role)}</span>
                  </div>
                </div>
              </div>

              <Alert>
                <AlertCircle className="h-4 w-4" />
                <AlertDescription>
                  You will be redirected to their tenant dashboard. You can exit impersonation at any time using the banner at the top of the page.
                </AlertDescription>
              </Alert>
            </div>
          )}

          <DialogFooter>
            <Button variant="outline" onClick={() => setShowImpersonateModal(false)}>
              Cancel
            </Button>
            <Button 
              onClick={confirmImpersonate} 
              disabled={impersonating}
              className="bg-orange-600 hover:bg-orange-700"
            >
              {impersonating ? 'Impersonating...' : 'Confirm Impersonation'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

packages/backend/src/services/cdr-activi.content:
// @ts-nocheck
import { v4 as uuidv4 } from 'uuid';
// import { VoipCdr, VoipActivityLog } from '@w3-voip/shared';

// Define types locally
export interface VoipCdr {
  id: string;
  tenant_id: string;
  store_id?: string;
  extension_id?: string;
  trunk_id?: string;
  call_uuid: string;
  direction: 'inbound' | 'outbound' | 'internal';
  caller_id: string;
  called_id: string;
  start_time: Date;
  answer_time?: Date;
  end_time?: Date;
  duration: number;
  bill_seconds: number;
  disposition: 'ANSWERED' | 'NO_ANSWER' | 'BUSY' | 'FAILED';
  hangup_cause: string;
  recording_path?: string;
  created_at: Date;
}

export interface VoipActivityLog {
  id: string;
  tenant_id: string;
  store_id?: string;
  extension_id?: string;
  trunk_id?: string;
  action: string;
  details: any;
  timestamp: Date;
  created_at: Date;
}

export interface TenantContext {
  tenant_id: string;
  sip_domain: string;
  store_id?: string;
}

export interface CdrFilters {
  tenant_id: string;
  store_id?: string;
  start_date?: string;
  end_date?: string;
  direction?: 'in' | 'out';
  disposition?: 'ANSWERED' | 'NO_ANSWER' | 'BUSY' | 'FAILED';
  ext_number?: string;
  did_e164?: string;
  limit?: number;
  offset?: number;
}

export interface ActivityLogFilters {
  tenant_id: string;
  actor?: string;
  action?: 'create' | 'update' | 'delete' | 'provision' | 'sync';
  target_type?: 'trunk' | 'did' | 'ext' | 'route' | 'policy';
  target_id?: string;
  status?: 'ok' | 'fail';
  start_date?: string;
  end_date?: string;
  limit?: number;
  offset?: number;
}

export class CdrActivityService {
  // Mock database - in production this would be real database operations
  private mockCdrs: VoipCdr[] = [];
  private mockActivityLogs: VoipActivityLog[] = [];

  // ===== VOIP CDR =====
  async createCdr(cdrData: Partial<VoipCdr>): Promise<VoipCdr> {
    const cdr: VoipCdr = {
      id: uuidv4(),
      tenant_id: cdrData.tenant_id!,
      store_id: cdrData.store_id,
      sip_domain: cdrData.sip_domain!,
      call_id: cdrData.call_id!,
      direction: cdrData.direction!,
      from_uri: cdrData.from_uri!,
      to_uri: cdrData.to_uri!,
      did_e164: cdrData.did_e164,
      ext_number: cdrData.ext_number,
      start_ts: cdrData.start_ts!,
      answer_ts: cdrData.answer_ts,
      end_ts: cdrData.end_ts!,
      billsec: cdrData.billsec || 0,
      disposition: cdrData.disposition!,
      recording_url: cdrData.recording_url,
      meta_json: cdrData.meta_json
    };

    this.mockCdrs.push(cdr);
    console.log(`Created CDR: ${cdr.call_id} for tenant: ${cdr.tenant_id}`);
    return cdr;
  }

  async getCdrs(filters: CdrFilters): Promise<{ cdrs: VoipCdr[]; total: number }> {
    let filteredCdrs = this.mockCdrs.filter(cdr => cdr.tenant_id === filters.tenant_id);

    // Apply filters
    if (filters.store_id) {
      filteredCdrs = filteredCdrs.filter(cdr => cdr.store_id === filters.store_id);
    }

    if (filters.direction) {
      filteredCdrs = filteredCdrs.filter(cdr => cdr.direction === filters.direction);
    }

    if (filters.disposition) {
      filteredCdrs = filteredCdrs.filter(cdr => cdr.disposition === filters.disposition);
    }

    if (filters.ext_number) {
      filteredCdrs = filteredCdrs.filter(cdr => cdr.ext_number === filters.ext_number);
    }

    if (filters.did_e164) {
      filteredCdrs = filteredCdrs.filter(cdr => cdr.did_e164 === filters.did_e164);
    }

    if (filters.start_date) {
      filteredCdrs = filteredCdrs.filter(cdr => cdr.start_ts >= filters.start_date!);
    }

    if (filters.end_date) {
      filteredCdrs = filteredCdrs.filter(cdr => cdr.start_ts <= filters.end_date!);
    }

    const total = filteredCdrs.length;

    // Apply pagination
    const offset = filters.offset || 0;
    const limit = filters.limit || 100;
    const paginatedCdrs = filteredCdrs.slice(offset, offset + limit);

    return { cdrs: paginatedCdrs, total };
  }

  async getCdrById(cdrId: string, tenantId: string): Promise<VoipCdr | null> {
    return this.mockCdrs.find(cdr => 
      cdr.id === cdrId && cdr.tenant_id === tenantId
    ) || null;
  }

  async getCdrStats(tenantId: string, storeId?: string, startDate?: string, endDate?: string): Promise<{
    total_calls: number;
    answered_calls: number;
    missed_calls: number;
    total_duration: number;
    avg_duration: number;
    by_direction: { inbound: number; outbound: number };
    by_disposition: Record<string, number>;
  }> {
    let filteredCdrs = this.mockCdrs.filter(cdr => cdr.tenant_id === tenantId);

    if (storeId) {
      filteredCdrs = filteredCdrs.filter(cdr => cdr.store_id === storeId);
    }

    if (startDate) {
      filteredCdrs = filteredCdrs.filter(cdr => cdr.start_ts >= startDate);
    }

    if (endDate) {
      filteredCdrs = filteredCdrs.filter(cdr => cdr.start_ts <= endDate);
    }

    const total_calls = filteredCdrs.length;
    const answered_calls = filteredCdrs.filter(cdr => cdr.disposition === 'ANSWERED').length;
    const missed_calls = total_calls - answered_calls;
    const total_duration = filteredCdrs.reduce((sum, cdr) => sum + cdr.billsec, 0);
    const avg_duration = total_calls > 0 ? total_duration / total_calls : 0;

    const by_direction = {
      inbound: filteredCdrs.filter(cdr => cdr.direction === 'in').length,
      outbound: filteredCdrs.filter(cdr => cdr.direction === 'out').length
    };

    const by_disposition = filteredCdrs.reduce((acc, cdr) => {
      acc[cdr.disposition] = (acc[cdr.disposition] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    return {
      total_calls,
      answered_calls,
      missed_calls,
      total_duration,
      avg_duration,
      by_direction,
      by_disposition
    };
  }

  // ===== VOIP ACTIVITY LOG =====
  async createActivityLog(tenantContext: TenantContext, logData: {
    actor: string;
    action: 'create' | 'update' | 'delete' | 'provision' | 'sync';
    target_type: 'trunk' | 'did' | 'ext' | 'route' | 'policy';
    target_id: string;
    status: 'ok' | 'fail';
    details_json?: any;
  }): Promise<VoipActivityLog> {
    const log: VoipActivityLog = {
      id: uuidv4(),
      tenant_id: tenantContext.tenant_id,
      actor: logData.actor,
      action: logData.action,
      target_type: logData.target_type,
      target_id: logData.target_id,
      status: logData.status,
      details_json: logData.details_json,
      ts: new Date().toISOString()
    };

    this.mockActivityLogs.push(log);
    console.log(`Created activity log: ${log.action} ${log.target_type} for tenant: ${log.tenant_id}`);
    return log;
  }

  async getActivityLogs(filters: ActivityLogFilters): Promise<{ logs: VoipActivityLog[]; total: number }> {
    let filteredLogs = this.mockActivityLogs.filter(log => log.tenant_id === filters.tenant_id);

    // Apply filters
    if (filters.actor) {
      filteredLogs = filteredLogs.filter(log => log.actor.includes(filters.actor!));
    }

    if (filters.action) {
      filteredLogs = filteredLogs.filter(log => log.action === filters.action);
    }

    if (filters.target_type) {
      filteredLogs = filteredLogs.filter(log => log.target_type === filters.target_type);
    }

    if (filters.target_id) {
      filteredLogs = filteredLogs.filter(log => log.target_id === filters.target_id);
    }

    if (filters.status) {
      filteredLogs = filteredLogs.filter(log => log.status === filters.status);
    }

    if (filters.start_date) {
      filteredLogs = filteredLogs.filter(log => log.ts >= filters.start_date!);
    }

    if (filters.end_date) {
      filteredLogs = filteredLogs.filter(log => log.ts <= filters.end_date!);
    }

    const total = filteredLogs.length;

    // Apply pagination
    const offset = filters.offset || 0;
    const limit = filters.limit || 100;
    const paginatedLogs = filteredLogs.slice(offset, offset + limit);

    return { logs: paginatedLogs, total };
  }

  async getActivityLogById(logId: string, tenantId: string): Promise<VoipActivityLog | null> {
    return this.mockActivityLogs.find(log => 
      log.id === logId && log.tenant_id === tenantId
    ) || null;
  }

  async getActivityStats(tenantId: string, startDate?: string, endDate?: string): Promise<{
    total_actions: number;
    successful_actions: number;
    failed_actions: number;
    by_action: Record<string, number>;
    by_target_type: Record<string, number>;
    by_actor: Record<string, number>;
  }> {
    let filteredLogs = this.mockActivityLogs.filter(log => log.tenant_id === tenantId);

    if (startDate) {
      filteredLogs = filteredLogs.filter(log => log.ts >= startDate);
    }

    if (endDate) {
      filteredLogs = filteredLogs.filter(log => log.ts <= endDate);
    }

    const total_actions = filteredLogs.length;
    const successful_actions = filteredLogs.filter(log => log.status === 'ok').length;
    const failed_actions = filteredLogs.filter(log => log.status === 'fail').length;

    const by_action = filteredLogs.reduce((acc, log) => {
      acc[log.action] = (acc[log.action] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    const by_target_type = filteredLogs.reduce((acc, log) => {
      acc[log.target_type] = (acc[log.target_type] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    const by_actor = filteredLogs.reduce((acc, log) => {
      acc[log.actor] = (acc[log.actor] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    return {
      total_actions,
      successful_actions,
      failed_actions,
      by_action,
      by_target_type,
      by_actor
    };
  }

  // ===== UTILITY METHODS =====
  async generateMockData(tenantId: string, sipDomain: string): Promise<void> {
    // Generate some mock CDRs
    const mockCdrs: Partial<VoipCdr>[] = [
      {
        tenant_id: tenantId,
        sip_domain: sipDomain,
        call_id: 'call-001',
        direction: 'inbound',
        from_uri: '+390686356924',
        to_uri: '1001',
        did_e164: '+390686356924',
        ext_number: '1001',
        start_ts: new Date(Date.now() - 3600000).toISOString(),
        answer_ts: new Date(Date.now() - 3595000).toISOString(),
        end_ts: new Date(Date.now() - 3000000).toISOString(),
        billsec: 600,
        disposition: 'ANSWERED',
        recording_url: 'https://recordings.example.com/call-001.wav',
        meta_json: { codec: 'G729', mos: 4.2 }
      },
      {
        tenant_id: tenantId,
        sip_domain: sipDomain,
        call_id: 'call-002',
        direction: 'outbound',
        from_uri: '1001',
        to_uri: '+39061234567',
        ext_number: '1001',
        start_ts: new Date(Date.now() - 1800000).toISOString(),
        end_ts: new Date(Date.now() - 1750000).toISOString(),
        billsec: 0,
        disposition: 'NO_ANSWER',
        meta_json: { codec: 'G729' }
      }
    ];

    for (const cdrData of mockCdrs) {
      await this.createCdr(cdrData);
    }

    // Generate some mock activity logs
    const mockLogs = [
      {
        actor: 'user:admin',
        action: 'create' as const,
