        details_json: { provider: 'Messagenet', proxy: 'sip.messagenet.it' }
      },
      {
        actor: 'user:admin',
        action: 'create' as const,
        target_type: 'ext' as const,
        target_id: 'ext-001',
        status: 'ok' as const,
        details_json: { ext_number: '1001', display_name: 'John Doe' }
      }
